const emailField=document.querySelector(".registration-form__email"),directionsForm=document.querySelector(".registration-form"),explanationText=document.querySelector(".registration-form__explanation-text");emailField.addEventListener("input",(e=>{e.target.value.length<1?explanationText.classList.add("registration-form__explanation-text--active"):explanationText.classList.remove("registration-form__explanation-text--active")})),directionsForm.addEventListener("submit",(e=>{emailField.value.length<1?(e.preventDefault(),explanationText.classList.add("registration-form__explanation-text--active"),emailField.focus()):(explanationText.classList.add("registration-form__explanation-text--active"),setTimeout((()=>explanationText.value=""),1e3))}));