const textFields=document.querySelectorAll(".entertainments__description"),directionsForm=document.querySelector(".plans-form");for(let e of textFields)e.addEventListener("input",(e=>{e.target.value.length<1?(e.target.classList.add("entertainments__description--invalid"),e.target.closest(".entertaments__wrapper").querySelector(".entertainments__invalid-text").classList.add("entertainments__invalid-text--active")):(e.target.classList.remove("entertainments__description--invalid"),e.target.closest(".entertaments__wrapper").querySelector(".entertainments__invalid-text").classList.remove("entertainments__invalid-text--active"))}));function fieldValidity(e){if(e.value.length<1)return e.classList.add("entertainments__description--invalid"),e.closest(".entertaments__wrapper").querySelector(".entertainments__invalid-text").classList.add("entertainments__invalid-text--active"),e.focus(),!0;e.classList.remove("entertainments__description--invalid"),e.closest(".entertaments__wrapper").querySelector(".entertainments__invalid-text").classList.remove("entertainments__invalid-text--active")}directionsForm.addEventListener("submit",(e=>{const t=Array.from(textFields);t.find((e=>fieldValidity(e)))?e.preventDefault():setTimeout((()=>t.map((e=>e.value=""))),1e3)}));